definition.script:
pipeline {
      agent any
      stages {
        stage('Prepare') {
          steps {
            sh "chomd +x ${env.DEPLOYMENT_HOME}/${env.JOB_NAME}/*.sh"
          }
        }
        stage('Config') {
          steps {
            sh "${env.DEPLOYMENT_HOME}/${env.JOB_NAME}/config.sh"
          }
        }
        stage('Build') {
          steps {
            sh "${env.DEPLOYMENT_HOME}/${env.JOB_NAME}/build.sh"
          }
        }
        stage('Deploy') {
          steps {
            sh "${env.DEPLOYMENT_HOME}/${env.JOB_NAME}/deploy.sh"
          }
        }
        stage('log') {
          steps {
            sh "${env.DEPLOYMENT_HOME}/${env.JOB_NAME}/deploy.sh"
          }
        }
      }
    }
